import React from 'react';
import Header from '../Header/Header';
import Footer from '../Footer/Footer';
import {Helmet} from 'react-helmet'
const Layout = ({children,title,description,keywords,author}) => {
    function createRandom() {
        var container = document.getElementById('main');
    
        // Check if the container element exists before proceeding
        if (container) {
            var borderArray = ['50%', '0'];
            var blursArray = ['0', '5px'];
            var colorsArray = ['#fff', '#FF6868', '#FFE66D'];
            var width = document.documentElement.clientWidth;
            var height = document.documentElement.clientHeight;
            var count = 20;
    
            for (var i = 0; i < count; i++) {
                var randomLeft = Math.floor(Math.random() * width);
                var randomTop = Math.floor(Math.random() * height);
                var color =Math.floor(Math.random()*3)
                var border = Math.floor(Math.random()*2)
                var blur = Math.floor(Math.random()*2)
                var widthElement = Math.floor(Math.random()*5)+5


                var div = document.createElement('div');
                div.style.background = colorsArray[color]
                div.style.position = 'absolute'
                div.style.width = widthElement + 'px';
                div.style.height = widthElement + 'px';
                div.style.marginLeft = randomLeft +'px'
                div.style.marginTop = randomTop +'px'
                div.style.borderRadius = borderArray[border]
                div.style.filter ='blur('+blursArray[blur]+')'
                div.style.zIndex= 10
                container.appendChild(div);
            }
        }
    }
    
    createRandom()
    
    return (
        <div style={{background:"#33333"}}>
            <Helmet>
                <meta charSet='utf-8' />
                <meta name='description' content={description} />
                <meta  name='keywords' content={keywords}/>
                <meta  name='author' content={author}/>
                <title>{title}</title>
            </Helmet>
            <div>
                <Header/>
                <main style={{minHeight:'100vh'}} className="wrapper" id='main'>
                    {children}
                </main>
                <Footer/>

            </div>
        </div>
    );
}

Layout.defaultProps = {
    title : 'Topzone app',
    description : "My project",
    keywords : "React js, node, mongoodb",
    author : "Anh Tuấn"
}

export default Layout;
