import React from 'react';
import Header from '../Header/Header';
import Footer from '../Footer/Footer';
import {Helmet} from 'react-helmet'
const Layout = ({children,title,description,keywords,author}) => {
    function createRandom() {
        var container = document.getElementById('main');
    
        // Check if the container element exists before proceeding
        if (container) {
            var borderArray = ['50%', '0'];
            var blursArray = ['0', '5px'];
            var colorsArray = ['#fff', '#FF6868', '#FFE66D'];
            var width = document.documentElement.clientWidth;
            var height = document.documentElement.clientHeight;
            var count = 20;
    
            for (var i = 0; i < count; i++) {
                var randomLeft = Math.floor(Math.random() * width);
                var randomTop = Math.floor(Math.random() * height);
    
                var div = document.createElement('div');
                div.style.left = randomLeft + 'px';
                div.style.top = randomTop + 'px';
                container.appendChild(div);
            }
        }
    }
    
    createRandom()
    
    return (
        <div className="wrapper" id='main'>
            <Helmet>
                <meta charSet='utf-8' />
                <meta name='description' content={description} />
                <meta  name='keywords' content={keywords}/>
                <meta  name='author' content={author}/>
                <title>{title}</title>
            </Helmet>
            <Header/>
            <main style={{minHeight:'100vh'}}>
                {children}
            </main>
            <Footer/>
        </div>
    );
}

Layout.defaultProps = {
    title : 'Topzone app',
    description : "My project",
    keywords : "React js, node, mongoodb",
    author : "Anh Tuấn"
}

export default Layout;
