import React from 'react';
import Header from '../Header/Header';
import Footer from '../Footer/Footer';
import {Helmet} from 'react-helmet'
const Layout = ({children,title,description,keywords,author}) => {
    var container =  document.getElementById('main')
    var borderArray = ['50%','0'] 
    var blursArray = ['0','5px'] // giọt mưa bị mờ
    var colorsArray =['#fff','#FF6868','#FFE66D']
    var width = document.documentElement.clientWidth // độ lớn độ ngang màn hình
    var height = document.documentElement.clientHeight // độ lớn độ ngang màn hình
    var count = 20 // tạo ra 20 giọt
    for(var i =0;i<count;i++){
        var randomLeft = Math.floor(Math.random()*width) // luôn năm trong màn hình khi rơi
        var randomTop = Math.floor(Math.random()*height) // luôn năm trong màn hình khi rơi
        
        var div = document.createElement("div")
    }
    container.appendChild(div)
    // function createRandom (){
    // } 
    // createRandom()
    
    return (
        <div>
            <Helmet>
                <meta charSet='utf-8' />
                <meta name='description' content={description} />
                <meta  name='keywords' content={keywords}/>
                <meta  name='author' content={author}/>
                <title>{title}</title>
            </Helmet>
            <div className="wrapper" id='main'>
                <Header/>
                <main style={{minHeight:'100vh'}}>
                    {children}
                </main>
                <Footer/>
            </div>
        </div>
    );
}

Layout.defaultProps = {
    title : 'Topzone app',
    description : "My project",
    keywords : "React js, node, mongoodb",
    author : "Anh Tuấn"
}

export default Layout;
